import React, { useState } from 'react';

const SearchBar = (props) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [options, setOptions] = useState([]);
  const [selectedOption, setSelectedOption] = useState(null);

  React.useEffect(() => (setOptions(props.data.rows.map((item) => {item[0]}))), []);
  const handleChange = (event) => {
    setSearchTerm(event.target.value);

    // search options based on the search term
    const filteredOptions = options.filter((option) => option.includes(searchTerm));
    setOptions(filteredOptions);
  };

  const handleOptionSelection = (event) => {
    setSelectedOption(event.target.value);
  };

  return (
    <div>
      <input type="text" value={searchTerm} onChange={handleChange} />

      <select value={selectedOption} onChange={handleOptionSelection}>
        {options.map((option) => (
          <option key={option} value={option}>
            {option}
          </option>
        ))}
      </select>
    </div>
  );
};

export default SearchBar;
