import React from 'react'
import Select from 'react-select'

const DeletePerson = (props) => {
	const data = props.data;
	//const options = data.rows.map((item) => item[0]);

	const options = [
	{ value: 'chocolate', label: 'Chocolate' },
	{ value: 'strawberry', label: 'Strawberry' },
	{ value: 'vanilla', label: 'Vanilla' }
	]
  return (
	<form className='myForm' id='deletePerson'>
		<div className="home-container-row ">
			<span className="home-text-header">Delete person:</span>
		</div>
		<div className="home-container-row">
			<div className="home-container-column-l">
			<span className="home-text">
				<span>Person's ID:</span>
				<br></br>
			</span>
			</div>
			<div className="home-container-column-r">
				{/* <input type="text" 
				placeholder="Enter name" 
				className="input"
				onChange={props.idToDeleteHandler} 
				name="name" /> */}
				<Select options={options} />
			</div>
		</div>


		<div className="home-container-row">
			<div className="home-container-column-l">
				<button type="submit" className="home-button button" onClick={props.deletePerson}>
				Delete person
				</button>
			</div>
			<div className="home-container-column-r">
			{(() => {
				if (props.userDeleted === 1) {
				return <span className="home-text" id="goodResult">✓ User deleted</span>
			} else if (props.userDeleted === -1) {
				return <span className="home-text" id="badResult">✗ The user doesn't exist!</span>;
			} else {
				return null;
			}})()}
			</div>

		</div>

	</form>
  )};

  export default DeletePerson;
