import React, { useState, useEffect } from 'react';

function TableWithPagination(props) {
  const [data, setData] = useState([]);
  const [page, setPage] = useState(1);
  const [pageSize, setPageSize] = useState(10);
  const [totalRows, setTotalRows] = useState(100);
  const [loading, setLoading] = useState(false);
  const [addPersonLoading, setAddPersonLoading] = useState(false);

  useEffect(() => {
    async function fetchData() {
      setLoading(true);
    fetch(`http://localhost:3001/db?getRowsCount=true`)  
      .then(response => response.json()) 
      .then(data => {
          setTotalRows(data.rows[0][0]);
        })
      .then(() => {
        fetch(`http://localhost:3001/db?getNRows&page=${page}&pageSize=${pageSize}`)  
        .then(response => response.json()) 
        .then(data => {
            setData(data.rows);
          });
      });

      setLoading(false);
    }
    fetchData();
  }, [page, pageSize, addPersonLoading]);

  function handlePageChange(newPage) {
    setPage(newPage);
  }

  function handlePageSizeChange(event) {
    setPageSize(event.target.value);
  }

  function addPersons(event, role) {
    event.preventDefault();
    console.log(event, role);
    // setAddPersonLoading(true);
    // fetch(`http://localhost:3001/db/generate?role=${role}`)
    // .then(response => response.json());
    // setAddPersonLoading(false);
  }

  return (
    <div className="mainTable">
      <form>
      {addPersonLoading ? (
        <div>Loading...</div>) : (
          <div className="addPeople">
            <button className="home-button button" id="addPerson" onClick={(event) => addPersons(event, 'manager')}>Add 10 managers</button>
            <button className="home-button button" id="addPerson" onClick={(event) => addPersons(event, 'lehrer')}>Add 10 teachers</button>
            <button className="home-button button" id="addPerson" onClick={(event) => addPersons(event, 'schueler')}>Add 10 students</button>
          </div>)
        }
        <label htmlFor="pageSize">Rows per page:</label>
        <select id="pageSize" value={pageSize} onChange={handlePageSizeChange}>
          <option value={10}>10</option>
          <option value={25}>25</option>
          <option value={50}>50</option>
        </select>
      </form>
      {loading ? (
        <div>Loading...</div>
      ) : (
        <table className="table table-bordered table-hover dt-responsive">
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Surname</th>
            </tr>
          </thead>
          <tbody>

            {data.map(item => (
              <tr key={item[0]}>
                <td>{item[0]}</td>
                <td>{item[1]}</td>
                <td>{item[2]}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
      <div>
        Page {page} of {Math.ceil(totalRows / pageSize)}
      </div>
      {page > 1 && (
        <button onClick={() => handlePageChange(page - 1)}>Previous</button>
      )}
      {page < Math.ceil(totalRows / pageSize) && (
        <button onClick={() => handlePageChange(page + 1)}>Next</button>
      )}
    </div>
  );
}

export default TableWithPagination;
