import React, {useState} from 'react'
import Select from 'react-select'

const DeletePerson = (props) => {

	const [options, setOptions] = useState([]);
	React.useEffect(() => {
		const data = props.data;
		let options = [];
		data.rows.map((item) => {
			options.push({ value: item[0], label: item[0] })
		})
		setOptions(options);
	}, [props.userDeleted]);

	const handleMenuClose = () => {
		setSelectedOption({ value: null, label: null});
	  };

	const [selectedOption, setSelectedOption] = useState({ value: null, label: null });
	const [isFocused, setIsFocused] = useState(false);
	const handleBlur = () => {
		setIsFocused(false);
	  };

	const handleFocus = () => {
		setIsFocused(true);
	  };

  	const handleChange = (selOpt) => {
    setSelectedOption(selOpt);
	if (selOpt.value !== null) {
		props.idToDeleteHandler(selOpt.value);
	}
  };

  return (
	<form className='myForm' id='deletePerson' onSubmit={props.deletePerson}>
		<div className="home-container-row ">
			<span className="home-text-header">Delete person:</span>
		</div>
		<div className="home-container-row">
			<div className="home-container-column-l">
			<span className="home-text">
				<span>Person's ID:</span>
				<br></br>
			</span>
			</div>
			<div className="home-container-column-r">
				{/* <input type="text" 
				placeholder="Enter name" 
				className="input"
				onChange={props.idToDeleteHandler} 
				name="name" /> */}
				{<Select 
				value={selectedOption} 
				options={options} 
				onChange={handleChange} 
				onMenuClose={isFocused ? handleMenuClose : undefined}
				isClearable={true} 
				onBlur={handleBlur}
				onFocus={handleFocus}
				/>}

			</div>
		</div>


		<div className="home-container-row">
			<div className="home-container-column-l">
				<button type="submit" className="home-button button">
				Delete person
				</button>
			</div>
			<div className="home-container-column-r">
			{(() => {
				if (props.userDeleted === 1) {
				return <span className="home-text" id="goodResult">✓ User deleted</span>
			} else if (props.userDeleted === -1) {
				return <span className="home-text" id="badResult">✗ The user doesn't exist!</span>;
			} else {
				return null;
			}})()}
			</div>

		</div>

	</form>
  )};

  export default DeletePerson;
