import React from 'react'

import './table.css'

const Table = (props) => {
  return (
	<div class="mainTable">
	<h2>Responsive Table with DataTables</h2>

		<table summary="This table shows how to create responsive tables using Datatables' extended functionality" class="table table-bordered table-hover dt-responsive">
			<caption class="text-center">An example of a responsive table based on <a href="https://datatables.net/extensions/responsive/" target="_blank">DataTables</a>:</caption>
			<thead>
			<tr>
				<th>Name</th>
				<th>Surname</th>
				<th>Role</th>
			</tr>
			</thead>
			<tbody>
			<tr>
				{/* map items from props to component called tableData with same props */}

				{props.data.map((item) => {
				return (<tableData data={props}/>)
				})}
				<td>Argentina</td>
				<td>Spanish (official)</td>
				<td>41,803,125</td>
			</tr>
			</tbody>
			<tfoot>
			</tfoot>
		</table>

	</div>
  )};

  export default Table;
